mapper.select:

select good_Id, good_name,good_image, good_type, good_price, stock, description,good_del_state
from goods
where good_Id = #{goodId,jdbcType=INTEGER}

select * from goods where good_del_state=0
and good_name like #{goodName}
and good_type = #{brandId}
and good_type in (select goods_type_id from goods_type where goods_type_parent_id = #{typeTwoId})
and good_type in (select goods_type_id from goods_type where goods_type_parent_id in(select goods_type_id from goods_type where goods_type_parent_id=#{typeOneId}))
order by good_type,good_Id

select *
from goods
where good_del_state=0 and good_Id = #{goodId,jdbcType=INTEGER}

mapper.update:
update goods
set good_del_state=1
where good_Id=#{ids}
