mapper.select:
select *
from users
where user_del_state = 0 and user_type>#{loginUserType}

  and user_name like #{userName}


  and user_type=#{userTypeId}

order by user_type, user_id

mapper.update:
update users
set user_del_state=1
where user_id=#{ids}

update users
set password=#{password}
where email = #{email}
